import styled from 'styled-components';

export const ComponentWrapper = styled.div`
    align-self: center;
    width: 50vw;
    min-height: 10rem;
    display: flex;

    #background {
        align-self: center;
    }

    #quote-box {
        color: ${props => props.colorFont};
    }
`
