import React, { useState, useEffect } from 'react';
import {fetchQuotes, Quotes} from './API';
import QuotesCard from './components/QuotesCard'

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [quote, setQuote] = useState<Quotes>(new Quotes())

  const startQuote = async() => {
    setLoading(true);

    const newQuote = await fetchQuotes();

    setQuote(newQuote);
  }
  useEffect(() => {
    if (quote.author === "" && quote.content === ""){
      startQuote();
      return
    }
    else {
    const timeQuote = setInterval(() => {
      startQuote();
    }, 5000);
    return () => clearInterval(timeQuote) 
  }
  });
  return (
    <>
      <div id="background">
        <QuotesCard
          author={quote.author}
          quote={quote.content} />
      </div>
    </>
  );
}

export default App;
