import styled from 'styled-components';

interface IComponentWrapper {
    col: string[];
}

export const ComponentWrapper = styled.div<IComponentWrapper>`
    align-self: center;

    #background {
        background-color: ${props => props.col[1]};
        width: 50vw;
        min-height: 10rem;
        display: flex;
        flex-direction: column;
    }

    #quote-box {
        color: ${props => props.col[0]};
        align-self: stretch;
        width: 100%;
        margin: 10vh 10vw;
    }

    #author {
        margin-top: 3vh;
    }
`
