import styled from 'styled-components';

interface IComponentWrapper {
    col: string[];
}

export const ComponentWrapper = styled.div<IComponentWrapper>`
    align-self: center;

    #background {
        background-color: ${props => props.col[1]};
        width: 50vw;
        min-height: 10rem;
        display: flex;
        flex-direction: column;
    }

    #quote-box {
        color: ${props => props.col[0]};
        align-self: stretch;
        width: auto;
        padding: 10vh 10vw;
        display: flex;
        flex-direction: column;
    }

    #author {
        margin-top: 3vh;
        align-self: end;
        width: fit-content;
    }

    .button-box {
        height: 15vh;
        display: flex;
        justify-content: flex-end;
    }

    .button-box button {
        align-self: center;
        margin-right: 3vw;
        padding: 1vh 1vw;
        box-shadow: 0 0 100px 1px red;
    }
`
