import React from 'react';
import { ComponentWrapper } from './QuotesCard.styles';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    IconLookup,
    IconDefinition,
    findIconDefinition
  } from '@fortawesome/fontawesome-svg-core'

  const coffeeLookup: IconLookup = { prefix: 'fas', iconName: 'coffee' }
  const coffeeIconDefinition: IconDefinition = findIconDefinition(coffeeLookup)
interface Props {
    author: string;
    quote: string;
    callback: React.MouseEventHandler;
    colors: string[];
}

const QuotesCard: React.FunctionComponent<Props> = ({
    author,
    quote,
    callback,
    colors
}) => {

    return (
        <>
            <ComponentWrapper col={colors}>
                <div id="background">
                    <div id="quote-box">
                        <div id="text">{quote}</div>
                        <div id="author">- {author}</div>
                    </div>
                    <div className="button-box">

                        <FontAwesomeIcon icon={coffeeIconDefinition} />
                        <button id="new-quote" onClick={callback}>New Quote</button>
                    </div>
                </div>
            </ComponentWrapper>
        </>
    )}

export default QuotesCard;
